// Funcionalidades básicas de emergencia mientras descargas Bootstrap
// Descarga Bootstrap real desde: https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/js/bootstrap.bundle.min.js

// Polyfill básico para navbar toggle
document.addEventListener('DOMContentLoaded', function() {
    const toggleBtn = document.querySelector('.navbar-toggler');
    const navbarCollapse = document.querySelector('.navbar-collapse');
    
    if (toggleBtn && navbarCollapse) {
        toggleBtn.addEventListener('click', function() {
            navbarCollapse.classList.toggle('show');
        });
    }
});

